package mine;

public class Ex10A_Z {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// %X는 16진수의 정수로 출력됨
		int i = 0;
		char AtoZ = 'a';
		 i = AtoZ;
		for(i = 'A' ; i <= 'Z' ; i ++) {
			System.out.printf( "%d 0x%X %c%n",i,i,i );
		}
		
//		%X는 16진수로 나타내는 포멧임
		
		
		
		
		
		
		
		
		
		
		
	}

}
